import React from "react";
import Airtable from "airtable";

const API_KEY = process.env.REACT_APP_API_KEY;
const BASE_ID = "app27OZGEFr5eKnk4";

class LastUpdatedCost extends React.Component {
  constructor() {
    super();

    this.state = {
      cost: "Loading...",
    };
  }

  async componentDidMount() {
    var base = new Airtable({ apiKey: API_KEY }).base(BASE_ID);
    var cost_temp = 0;

    base("Boba Tracker")
      .select({
        view: "Grid view",
      })
      .eachPage(
        function page(records, fetchNextPage) {
          // This function (`page`) will get called for each page of records.
          records.forEach(function (record) {
            cost_temp += record.get("price");
          });

          // To fetch the next page of records, call `fetchNextPage`.
          // If there are more records, `page` will get called again.
          // If there are no more records, `done` will get called.
          fetchNextPage();
        },
        function done(err) {
          console.log("total cost is " + cost_temp);
          this.setState({
            cost: 5,
          });

          if (err) {
            console.error(err);
            return;
          }
        }
      );

    (async () => {
      console.log("Getting total boba cost...");
      while (lastUpdatedDate == undefined)
        // define the condition as you like
        await new Promise((resolve) => setTimeout(resolve, 1000));
      console.log("lastUpdated is " + lastUpdatedDate);
      this.setState({
        date: lastUpdatedDate,
      });
    })();
  }

  render() {
    return <span>{this.state.cost}</span>;
  }
}

export default LastUpdatedCost;
