import React from "react";
import { Container, Row, Col } from "shards-react";
import { Form, FormGroup, FormInput, FormRadio } from "shards-react";

class Daily extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      dailyRating: null,
    };

    this.changeRating = this.changeRating.bind(this);
  }

  changeRating(rating) {
    this.setState({
      dailyRating: rating,
    });
  }

  render() {
    return (
      <Container className="internalBlock">
        <Row>
          <Col>
            <h1>&#128171; Rating</h1>
          </Col>
        </Row>
        <Row>
          <Col>
            <h4>how was your day:</h4>
            <FormRadio
              inline
              name="rating"
              checked={this.state.dailyRating === "1"}
              onChange={() => {
                this.changeRating("1");
              }}
            >
              <p>1</p>
            </FormRadio>
            <FormRadio
              inline
              name="rating"
              checked={this.state.dailyRating === "2"}
              onChange={() => {
                this.changeRating("2");
              }}
            >
              2
            </FormRadio>
            <FormRadio
              inline
              name="rating"
              checked={this.state.dailyRating === "3"}
              onChange={() => {
                this.changeRating("3");
              }}
            >
              3
            </FormRadio>
            <FormRadio
              inline
              name="rating"
              checked={this.state.dailyRating === "4"}
              onChange={() => {
                this.changeRating("4");
              }}
            >
              4
            </FormRadio>
            <FormRadio
              inline
              name="rating"
              checked={this.state.dailyRating === "5"}
              onChange={() => {
                this.changeRating("5");
              }}
            >
              5
            </FormRadio>
          </Col>
        </Row>
        <Row>
          <Col>
            <h4>reason:</h4>
          </Col>
        </Row>
        <Row>
          <Col>
            <Form>
              <FormGroup>
                <FormInput id="#reason" placeholder="Reason" />
              </FormGroup>
            </Form>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Daily;
