import React from "react";
import { Container, Row, Col } from "shards-react";
import { Form, FormGroup, FormInput, Slider, Button } from "shards-react";

class BobaTracker extends React.Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  render() {
    return (
      <Container className="internalBlock">
        <Row className="internalBlockSection">
          <Col>
            <h1>&#129483; Boba Log</h1>
          </Col>
        </Row>
        <Row className="internalBlockSection">
          <Col>
            <h4>Current data:</h4>
            <p>
              Cost: <span className="data">{this.state.cost || "$xx.xx"}</span>
            </p>
            <p>
              Count:{" "}
              <span className="data">{this.state.count || "x cups"}</span>{" "}
            </p>
            <p>
              Last Drank:{" "}
              <span className="data">
                {this.state.lastDrank || "mm-dd-yyyy"}
              </span>
            </p>
          </Col>
        </Row>
        <Row className="internalBlockSection">
          <Col>
            <h4>New entry:</h4>
            <Form>
              <FormGroup>
                <label htmlFor="financials">
                  <p>Describe your day in one sentence.</p>
                </label>
                <FormInput id="#reason" placeholder="Reason" />
              </FormGroup>
            </Form>
          </Col>
        </Row>
        <Row className="internalBlockSection">
          <Col>
            <Button theme="dark">
              <p>&#128640; Submit</p>
            </Button>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default BobaTracker;
