import React from "react";
import Airtable from "airtable";

const API_KEY = process.env.REACT_APP_API_KEY;
const BASE_ID = "app27OZGEFr5eKnk4";

class LastUpdated extends React.Component {
  constructor() {
    super();

    this.state = {
      date: "Loading...",
    };
  }

  async componentDidMount() {
    var base = new Airtable({ apiKey: API_KEY }).base(BASE_ID);
    var lastUpdatedDate = undefined;

    base("Dailys")
      .select({
        view: "Grid view",
        sort: [{ field: "date", direction: "desc" }],
      })
      .eachPage(
        function page(records, next) {
          try {
            console.log(records[0].fields.date);
            lastUpdatedDate = records[0].fields.date;
            console.log("retrieved: " + lastUpdatedDate);
            this.setState({
              date: lastUpdatedDate,
            });
            console.log("date is set to " + lastUpdatedDate);
            next();
          } catch {
            return;
          }
        },
        function (err) {
          if (err) {
            console.error(err);
            return;
          }
        }
      );
    await new Promise((resolve) => {
      setTimeout(resolve, 1000);
    });
    this.setState({
      date: lastUpdatedDate,
    });
    console.log(this.state.date);
  }

  render() {
    return (
      <div>
        <p>{this.state.date}</p>
        <p>hi</p>
      </div>
    );
  }
}

export default LastUpdated;
