import { setDate } from "date-fns";
import React from "react";
import { Container, Row, Col } from "shards-react";

class Heading extends React.Component {
  constructor() {
    super();
  }

  componentDidMount() {
    //set date of placeholder
    this.setDate();
  }

  setDate() {
    let date = new Date();
    date.toISOString().split("T")[0];
    alert(date);
  }

  render() {
    return (
      <Container>
        <Row>
          <Col>
            <h1>&#127804; Daily's</h1>
          </Col>
        </Row>
        <Row>
          <Col>
            <h3>
              today's date: <span>mm-dd-yyyy</span>
            </h3>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Heading;
