import React from "react";
import { Container, Row, Col } from "shards-react";
import { Form, FormGroup, FormInput, Slider } from "shards-react";

class Daily extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      dailyRating: 50,
    };
    this.handleSlide = this.handleSlide.bind(this);

    var date = new Date();
  }

  handleSlide(e) {
    this.setState({
      dailyRating: parseFloat(e[0]),
    });
  }

  render() {
    return (
      <Container className="internalBlock">
        <Row className="internalBlockSection">
          <Col>
            <h1>&#128171; Rating</h1>
            <p>Last Updated: {date}</p>
          </Col>
        </Row>
        <Row className="internalBlockSection">
          <Col sm="12" lg="8">
            <h4>How was your day?</h4>
            <p>Value: {this.state.dailyRating}</p>
            <Slider
              onSlide={this.handleSlide}
              connect={[true, false]}
              start={[this.state.dailyRating]}
              range={{ min: 0, max: 100 }}
              pips={{ mode: "steps", stepped: true, density: 20 }}
            />
          </Col>
        </Row>
        <Row className="internalBlockSection">
          <Col>
            <h4>reason:</h4>
          </Col>
        </Row>
        <Row>
          <Col>
            <Form>
              <FormGroup>
                <FormInput id="#reason" placeholder="Reason" />
              </FormGroup>
            </Form>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Daily;
