import React from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardHeader,
  CardTitle,
  CardBody,
  CardFooter,
  InputGroup,
  InputGroupText,
  InputGroupAddon,
  Form,
  FormGroup,
  FormInput,
  Button,
} from "shards-react";

class CoffeeTrackerCard extends React.Component {
  constructor(props) {
    super(props);

    this.handleSlide = this.handleSlide.bind(this);
    this.state = {
      lastDrank: null,
      count: null,
      cost: null,
      newEntry_drink: null,
      newEntry_extras: null,
      newEntry_price: null,
      newEntry_store: null,
    };
  }

  changeHandler = (e) => {
    this.setState({
      newEntry_store: e.target.value,
    });
  };

  submitForm = () => {
    console.log(this.state.newEntry_store);
  };

  render() {
    return (
      <Card style={{ maxWidth: "450px" }}>
        <CardHeader>
          <h1>&#9749; Coffee</h1>
        </CardHeader>
        <CardBody style={{ paddingTop: "0", paddingBottom: "0" }}>
          <Container className="internalBlock">
            <Row className="internalBlockSection">
              <Col>
                <h4>Current data:</h4>
                <p>
                  Cost:{" "}
                  <span className="data">{this.state.cost || "$xx.xx"}</span>
                </p>
                <p>
                  Count:{" "}
                  <span className="data">{this.state.count || "x cups"}</span>{" "}
                </p>
                <p>
                  Last Drank:{" "}
                  <span className="data">
                    {this.state.lastDrank || "mm-dd-yyyy"}
                  </span>
                </p>
              </Col>
            </Row>
            <Row className="internalBlockSection">
              <Col>
                <h4>New entry:</h4>
                <Form>
                  <FormGroup>
                    {/* Store Name */}
                    <label htmlFor="store">
                      <p>Store</p>
                    </label>
                    <FormInput
                      value={this.state.newEntry_store || ""}
                      name="newEntry_store"
                      type="text"
                      onChange={this.changeHandler}
                      id="#store"
                      placeholder="e.g. Omomo"
                    />

                    {/* Drink Name */}
                    <label htmlFor="drink">
                      <p>Drink</p>
                    </label>
                    <FormInput
                      value={this.state.newEntry_drink || ""}
                      name="newEntry_drink"
                      type="text"
                      onChange={this.changeHandler}
                      id="#drink"
                      placeholder="e.g. Jasmine Milk Tea"
                    />

                    {/* Extras */}
                    <label htmlFor="toppings">
                      <p>Extras</p>
                    </label>
                    <FormInput
                      value={this.state.newEntry_extras || ""}
                      name="newEntry_extras"
                      type="text"
                      onChange={this.changeHandler}
                      id="#toppings"
                      placeholder="e.g. Oat Milk"
                    />

                    {/* Price */}
                    <label htmlFor="price">
                      <p>Price</p>
                    </label>
                    <InputGroup id="#price" className="mb-2">
                      <InputGroupAddon type="prepend">
                        <InputGroupText>$</InputGroupText>
                      </InputGroupAddon>
                      <FormInput
                        value={this.state.newEntry_price || ""}
                        name="newEntry_price"
                        type="number"
                        onChange={this.changeHandler}
                        placeholder="xx.xx"
                      />
                    </InputGroup>
                  </FormGroup>
                </Form>
              </Col>
            </Row>
          </Container>
        </CardBody>
        <CardFooter>
          <Container>
            <Row>
              <Col>
                <Button onClick={this.submitForm} theme="dark">
                  <p>&#128640; Submit</p>
                </Button>
              </Col>
            </Row>
          </Container>
        </CardFooter>
      </Card>
    );
  }
}

export default CoffeeTrackerCard;
