import React from "react";
import { Container, Row, Col } from "shards-react";
import { FormRadio } from "shards-react";

class Daily extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      dailyRating: null,
    };

    this.changeRating = this.changeRating.bind(this);
  }

  changeRating(rating) {
    this.setState({
      dailyRating: rating,
    });
  }

  render() {
    return (
      <Container>
        <Row>
          <Col>
            <h1>&#128171; Rating</h1>
          </Col>
        </Row>
        <Row>
          <Col>
            <p>How was your day:</p>
            <FormRadio
              inline
              name="rating"
              checked={this.state.dailyRating === "1"}
              onChange={() => {
                this.changeRating("1");
              }}
            >
              1
            </FormRadio>
            <FormRadio
              inline
              name="rating"
              checked={this.state.dailyRating === "2"}
              onChange={() => {
                this.changeRating("2");
              }}
            >
              2
            </FormRadio>
            <FormRadio
              inline
              name="rating"
              checked={this.state.dailyRating === "3"}
              onChange={() => {
                this.changeRating("3");
              }}
            >
              3
            </FormRadio>
            <FormRadio
              inline
              name="rating"
              checked={this.state.dailyRating === "4"}
              onChange={() => {
                this.changeRating("4");
              }}
            >
              4
            </FormRadio>
            <FormRadio
              inline
              name="rating"
              checked={this.state.dailyRating === "5"}
              onChange={() => {
                this.changeRating("5");
              }}
            >
              5
            </FormRadio>
          </Col>
        </Row>
        <Row>
          <Col>
            <span>
              <strong>Selected rating:</strong>{" "}
              <span>{this.state.dailyRating || "none"}</span>
            </span>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Daily;
